import { type Connection, type MongoError } from 'mongodb/src';
import type { Document } from 'mongodb/src/bson';
import { MongoDBResponse } from 'mongodb/src/cmap/wire_protocol/responses';
import type { Collection } from 'mongodb/src/collection';
import { MONGODB_ERROR_CODES, MongoServerError } from 'mongodb/src/error';
import type { ServerCommandOptions } from 'mongodb/src/sdam/server';
import type { ClientSession } from 'mongodb/src/sessions';
import { type TimeoutContext } from 'mongodb/src/timeout';
import { AbstractOperation } from 'mongodb/src/operations/operation';

/** @internal */
export class DropSearchIndexOperation extends AbstractOperation<void> {
  override SERVER_COMMAND_RESPONSE_TYPE = MongoDBResponse;

  private readonly collection: Collection;
  private readonly name: string;

  constructor(collection: Collection, name: string) {
    super();
    this.collection = collection;
    this.name = name;
    this.ns = collection.fullNamespace;
  }

  override get commandName() {
    return 'dropSearchIndex' as const;
  }

  override buildCommand(_connection: Connection, _session?: ClientSession): Document {
    const namespace = this.collection.fullNamespace;

    const command: Document = {
      dropSearchIndex: namespace.collection
    };

    if (typeof this.name === 'string') {
      command.name = this.name;
    }

    return command;
  }

  override handleOk(_response: MongoDBResponse): void {
    // do nothing
  }

  override buildOptions(timeoutContext: TimeoutContext): ServerCommandOptions {
    return { session: this.session, timeoutContext };
  }

  override handleError(error: MongoError): void {
    const isNamespaceNotFoundError =
      error instanceof MongoServerError && error.code === MONGODB_ERROR_CODES.NamespaceNotFound;
    if (!isNamespaceNotFoundError) {
      throw error;
    }
  }
}
